svg.script:
/* pour l'animation en js
source du script:
http://samples.msdn.microsoft.com/workshop/samples/svg/svgAnimation/basic/02scriptSquare.html
*/
// couleurs
const couleurs =[ 'F00', 'B40', '880', '4B0', '0F0', '0B4', '088', '04B', '00F', '40B', '808', 'B04' ];
const couleursId =[ 0, 6, 0, 0 ];
const echantillons = document.getElementsByTagName ('rect');
const couleursNoms = document.getElementsByTagName ('text');
// rotation
var angle =75;
var nbRotation =0;
const saut =3;
var obj = document.getElementById ('aiguille-complementaire');
var tourner = null;
obj.addEventListener ('mouseover', rotationEventAiguilleDeux);
function rotationEventAiguilleTroisA (event){
	obj = event.target;
	couleursId[0] +=1;
	if (couleursId[0] >= couleurs.length) couleursId[0] =0;
	couleursId[1] = couleursId[0] +6;
	if (couleursId[1] >= couleurs.length) couleursId[1] -= couleurs.length;
	tourner = requestAnimationFrame (rotationAnimation);
	echantillons[0].style.fill = '#'+ couleurs[couleursId[0]];
	echantillons[1].style.fill = '#'+ couleurs[couleursId[1]];
	couleursNoms[0].innerHTML = '#'+ couleurs[couleursId[0]];
	couleursNoms[1].innerHTML = '#'+ couleurs[couleursId[1]];
}
function rotationEventAiguilleDeux (event){
	obj = event.target;
	couleursId[0] +=1;
	if (couleursId[0] >= couleurs.length) couleursId[0] =0;
	couleursId[1] = couleursId[0] +6;
	if (couleursId[1] >= couleurs.length) couleursId[1] -= couleurs.length;
	tourner = requestAnimationFrame (rotationAnimation);
	echantillons[0].style.fill = '#'+ couleurs[couleursId[0]];
	echantillons[1].style.fill = '#'+ couleurs[couleursId[1]];
	couleursNoms[0].innerHTML = '#'+ couleurs[couleursId[0]];
	couleursNoms[1].innerHTML = '#'+ couleurs[couleursId[1]];
}
function rotationAnimation(){
	nbRotation +=1;
	angle = angle + saut;
	obj.setAttributeNS (null, 'transform', 'rotate(' + angle + ',0,0)');
	if (nbRotation >9) nbRotation =0;
	else tourner = requestAnimationFrame (rotationAnimation);
}
